import "./TicTacTeo.css";
import circle from "../assests/circle.png";
import cross from "../assests/cross.png";
import { useState } from "react";

let data = ["", "", "", "", "", "", "", "", ""];

export default function TicTacTeo() {
	let [count, setCount] = useState(0);
	let [lock, setLock] = useState(false);
	const toggle = (w, num) => {
		if (lock) {
			return 0;
		}
		if (count % 2 === 0) {
			w.target.innerHTML = `<img src ='${cross}'>`;
			data[num] = "x";
			setCount(++count);
		} else {
			w.target.innerHTML = `<img src ='${circle}'>`;
			data[num] = "o";
			setCount(++count);
		}
	};
	return (
		<div className="container">
			<div className="title">
				<h1>
					Tic Tac Toe game In <span>React</span>
				</h1>
			</div>
			<div className="board">
				<div className="row1">
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 0);
						}}
					></div>
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 1);
						}}
					></div>
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 2);
						}}
					></div>
				</div>

				<div className="row2">
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 3);
						}}
					></div>
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 4);
						}}
					></div>
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 5);
						}}
					></div>
				</div>

				<div className="row3">
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 6);
						}}
					></div>
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 7);
						}}
					></div>
					<div
						className="boxes"
						onClick={(e) => {
							toggle(e, 8);
						}}
					></div>
				</div>
			</div>
			<button className="reset">Reset</button>
		</div>
	);
}
